import { Typography, Avatar, Grid, useTheme } from '@mui/material';
import { useEth } from '../contexts/EthContext';
import Creation from './Creation';
import { styled } from '@mui/system'; // Import styled from @mui/system

// Create a custom Typography component with modified font styles
const CustomTypography = styled(Typography)`
  font-family: 'Arial', sans-serif;
  font-weight: 600;
`;

function PageHeader({ refetchData }) {
  const {
    state: { accounts },
  } = useEth();
  const user = {
    avatar: `https://avatars.dicebear.com/api/pixel-art-neutral/${
      accounts === null ? '1' : accounts[0]
    }.svg`,
  };
  const theme = useTheme();

  return (
    <Grid container alignItems="center">
      <Grid item>
        <Avatar
          sx={{
            mr: 2,
            width: theme.spacing(12),
            height: theme.spacing(12),
          }}
          variant="rounded"
          alt={user.name}
          src={user.avatar}
        />
      </Grid>
      <Grid item>
        <Typography variant="h3" component="h3" gutterBottom>
          WELCOME
        </Typography>
        <Typography variant="subtitle2">
          Participate in NFT auctions now!
        </Typography>
        <Creation refetchData={refetchData} />
      </Grid>
    </Grid>
  );
}

export default PageHeader;
