import React, { useState } from "react";
import {
  Card,
  CardContent,
  Typography,
  TextField,
  Button,
  Box,
} from "@mui/material";
import pinImage from "../pinNFT";
const path = require("path");

function FileUploadButton() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [pinataApiKey, setPinataApiKey] = useState("");
  const [pinataSecretApiKey, setPinataSecretApiKey] = useState("");

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleFileUpload = () => {
    if (selectedFile) {
      console.log("Uploaded:", selectedFile);
      console.log("Pinata API Key:", pinataApiKey);
      console.log("Pinata Secret API Key:", pinataSecretApiKey);

    const filePath = path.join(__dirname, "../assets/peppa.jpg");

      saveFileToAssets(selectedFile)
        .then((filePath) => {
          console.log("File saved to assets:", filePath);
          // Perform other actions with the saved file
          pinImage(filePath, pinataApiKey, pinataSecretApiKey);
        })
        .catch((error) => {
          console.log("Error saving file to assets:", error);
        });

      setSelectedFile(null);
      setPinataApiKey("");
      setPinataSecretApiKey("");
    }
  };

  const saveFileToAssets = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();

      reader.onload = (event) => {
        const fileContents = event.target.result;

        const element = document.createElement("a");
        element.href = URL.createObjectURL(file);
        element.download = file.name;
        element.click();

        resolve(file.name);
      };

      reader.onerror = (event) => {
        reject(event.target.error);
      };

      reader.readAsDataURL(file);
    });
  };

  return (
    <Box border={1} borderRadius={4} p={2} mt={2}>
      <Card>
        <CardContent>
          <Typography variant="h6" component="div" gutterBottom>
            <strong style={{ fontSize: "1.5rem" }}>
              Upload and mint a NFT
            </strong>
          </Typography>
          <TextField
            label="Pinata API Key"
            value={pinataApiKey}
            onChange={(e) => setPinataApiKey(e.target.value)}
            fullWidth
            margin="normal"
          />
          <TextField
            label="Pinata Secret API Key"
            value={pinataSecretApiKey}
            onChange={(e) => setPinataSecretApiKey(e.target.value)}
            fullWidth
            margin="normal"
          />
          <input
            id="upload-input"
            type="file"
            onChange={handleFileSelect}
            style={{ display: "none" }}
          />
          <label htmlFor="upload-input">
            <Button
              component="span"
              variant="contained"
              color="primary"
              fullWidth
              size="large"
              style={{ fontWeight: "bold", marginTop: 10 }}
            >
              Choose File
            </Button>
          </label>
          <Button
            onClick={handleFileUpload}
            disabled={!selectedFile || !pinataApiKey || !pinataSecretApiKey}
            variant="contained"
            color="primary"
            fullWidth
            size="large"
            style={{
              fontWeight: "bold",
              marginTop: 10,
              backgroundColor: "green",
            }}
          >
            Upload
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
}

export default FileUploadButton;
