import React, { useState } from "react";
import {
  Card,
  CardContent,
  Typography,
  TextField,
  Button,
  Box,
} from "@mui/material";

import pinImage from "../pinNFT";
var streamifier = require("streamifier");

function NFTUpload() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [pinataApiKey, setPinataApiKey] = useState("");
  const [pinataSecretApiKey, setPinataSecretApiKey] = useState("");

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleFileUpload = () => {
    if (selectedFile) {
      const reader = new FileReader();
      reader.onload = () => {
        const dataUrl = reader.result;
        const base64Url = dataUrl.replace(/^data:image\/png;base64,/, "");
        const buffer = Buffer.from(base64Url, "base64");
        const readStream = streamifier.createReadStream(buffer);

        console.log(base64Url);
        console.log(readStream);
        // pinImage(readStream, pinataApiKey, pinataSecretApiKey);
      };
      reader.readAsDataURL(selectedFile);

      setSelectedFile(null);
      setPinataApiKey("");
      setPinataSecretApiKey("");
    }
  };

  return (
    <Box border={1} borderRadius={4} p={2} mt={2}>
      <Card>
        <CardContent>
          <Typography variant="h6" component="div" gutterBottom>
            <strong style={{ fontSize: "1rem" }}>
              Upload and mint a NFT
            </strong>
          </Typography>
          <input
            id="upload-input"
            type="file"
            onChange={handleFileSelect}
            style={{ display: "none" }}
          />
          <label htmlFor="upload-input">
            <Button
              component="span"
              variant="contained"
              color="primary"
              fullWidth
              size="large"
              style={{ fontWeight: "bold", marginTop: 10 }}
            >
              Choose File
            </Button>
          </label>
          <Button
            onClick={handleFileUpload}
            disabled={!selectedFile || !pinataApiKey || !pinataSecretApiKey}
            variant="contained"
            color="primary"
            fullWidth
            size="large"
            style={{
              fontWeight: "bold",
              marginTop: 10,
              backgroundColor: "green",
            }}
          >
            Upload
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
}

export default NFTUpload;
