require("dotenv").config();
const fs = require("fs");
const axios = require("axios");
const FormData = require("form-data");
const { storeDataToFile } = require("../scripts/ipfsHelper.js");

const pinataEndpoint = process.env.PINATA_ENDPOINT;

const pinImage = async (selectedFile, pinataApiKey, pinataSecretApiKey) => {
  const form_data = new FormData();
  try {
    form_data.append("file", selectedFile);
    const request = {
      method: "post",
      url: pinataEndpoint,
      maxContentLength: "Infinity",
      headers: {
        pinata_api_key: pinataApiKey,
        pinata_secret_api_key: pinataSecretApiKey,
        "Content-Type": `multipart/form-data; boundary=${form_data._boundary}`,
      },
      data: form_data,
    };
    const response = axios(request);
    storeDataToFile(response.data);
    console.log("Successfully pinned image, response added to JSON file");
  } catch (err) {
    console.log("Error occurred while pinning NFT to IPFS: ", err);
  }
};
