import React, { useState } from "react";
import { Card, CardContent, Typography, Button, Box } from "@mui/material";
import pinImage from "../scripts/pinImage.js";
const streamifier = require("streamifier");

function NFTUpload() {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleFileUpload = () => {
    if (selectedFile) {
      debugger;
      const reader = new FileReader();
      reader.onload = () => {
        const blob = new Blob([reader.result], { type: selectedFile.type });
        const fileUrl = URL.createObjectURL(blob);
        const fileName = selectedFile.name;

        const link = document.createElement("a");
        link.href = fileUrl;
        link.download = fileName;
        link.click();

        debugger;
        pinImage(fileUrl); // Pass the file URL to pinImage

        // const dataUrl = reader.result;
        // const base64Url = dataUrl.replace(/^data:image\/png;base64,/, "");
        // const buffer = Buffer.from(base64Url, "base64");
        // const readStream = streamifier.createReadStream(buffer);

        // pinImage(readStream);

        setSelectedFile(null);
      };
      // reader.readAsDataURL(selectedFile);
      reader.readAsArrayBuffer(selectedFile);
    }
  };

  return (
    <Box border={1} borderRadius={4} p={2} mt={2}>
      <Card>
        <CardContent>
          <Typography variant="h6" component="div" gutterBottom>
            <strong style={{ fontSize: "1.5rem" }}>
              Upload and mint a NFT
            </strong>
          </Typography>
          <input
            id="upload-input"
            type="file"
            onChange={handleFileSelect}
            style={{ display: "none" }}
          />
          <label htmlFor="upload-input">
            <Button
              component="span"
              variant="contained"
              color="primary"
              fullWidth
              size="large"
              style={{ fontWeight: "bold", marginTop: 10 }}
            >
              Choose File
            </Button>
          </label>
          <Button
            onClick={handleFileUpload}
            disabled={!selectedFile}
            variant="contained"
            color="primary"
            fullWidth
            size="large"
            style={{
              fontWeight: "bold",
              marginTop: 10,
              backgroundColor: "green",
            }}
          >
            Upload
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
}

export default NFTUpload;
